/* Admin Dashboard Specific Styles */

/* Danger Theme */
.btn-danger {
    background-color: #dc3545;
    border-color: #dc3545;
    color: white;
    transition: all 0.3s ease;
}

.btn-danger:hover {
    background-color: #bb2d3b;
    border-color: #b02a37;
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(220, 53, 69, 0.3);
}

/* Sidebar Active Item */
.sidebar .list-group-item.active {
    background-color: rgba(220, 53, 69, 0.1);
    color: #dc3545;
    border-left-color: #dc3545;
}

/* Border Left Colors */
.border-left-danger {
    border-left: 4px solid #dc3545 !important;
}

.border-left-dark {
    border-left: 4px solid #212529 !important;
}

/* Dashboard Cards */
.dashboard-card {
    border-radius: 10px;
    overflow: hidden;
    transition: all 0.3s ease;
    height: 100%;
}

.dashboard-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.15);
}

.dashboard-card-header {
    padding: 15px 20px;
    font-weight: 600;
}

/* Activity Timeline */
.activity-timeline {
    position: relative;
    padding-left: 0;
    max-height: 280px;
    overflow-y: auto;
    overflow-x: hidden;
}

/* Red Scrollbar for Activity Timeline */
.activity-timeline::-webkit-scrollbar {
    width: 6px;
}

.activity-timeline::-webkit-scrollbar-track {
    background: #f1f1f1;
    border-radius: 10px;
}

.activity-timeline::-webkit-scrollbar-thumb {
    background: #dc3545;
    border-radius: 10px;
}

.activity-timeline::-webkit-scrollbar-thumb:hover {
    background: #bb2d3b;
}

/* .activity-timeline::before removed */

.activity-item {
    position: relative;
    padding-bottom: 20px;
}

.activity-item:last-child {
    padding-bottom: 0;
}

/* .activity-item::before removed */

.activity-icon {
    width: 40px;
    height: 40px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: rgba(220, 53, 69, 0.1);
}

/* Table Styles */
.table th {
    background-color: rgba(0, 0, 0, 0.03);
    font-weight: 600;
    color: #333;
    border-top: none;
}

/* Sticky table headers inside scroll containers */
.scroll-container .table thead th {
    position: sticky;
    top: 0;
    background-color: #f8f9fa;
    z-index: 2;
    box-shadow: 0 1px 2px rgba(0, 0, 0, 0.1);
}

.table-hover tbody tr:hover {
    background-color: rgba(220, 53, 69, 0.05);
}

/* Scroll Container */
.scroll-container {
    max-height: 320px;
    /* Shows approximately 6 rows before scrolling */
    overflow-y: auto;
    overflow-x: hidden;
}

/* Custom Red Scrollbar */
.scroll-container::-webkit-scrollbar {
    width: 6px;
}

.scroll-container::-webkit-scrollbar-track {
    background: #f1f1f1;
    border-radius: 10px;
}

.scroll-container::-webkit-scrollbar-thumb {
    background: #dc3545;
    border-radius: 10px;
}

.scroll-container::-webkit-scrollbar-thumb:hover {
    background: #bb2d3b;
}

/* Status Badges */
.badge-status {
    padding: 5px 10px;
    border-radius: 20px;
    font-size: 0.8em;
}

/* Report Preview */
.report-preview {
    background-color: white;
    border: 1px solid #dee2e6;
    border-radius: 5px;
    padding: 20px;
    min-height: 400px;
}

/* Backup Cards */
.backup-card {
    transition: all 0.3s ease;
    height: 100%;
}

.backup-card:hover {
    transform: translateY(-3px);
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
}

/* Request Tabs */
.nav-tabs .nav-link {
    color: #495057;
    border: 1px solid transparent;
    border-top-left-radius: 0.25rem;
    border-top-right-radius: 0.25rem;
}

.nav-tabs .nav-link:hover {
    border-color: #e9ecef #e9ecef #dee2e6;
}

.nav-tabs .nav-link.active {
    color: #dc3545;
    background-color: white;
    border-color: #dee2e6 #dee2e6 white;
}

/* Dark Header Tabs */
.card-header.bg-black .nav-tabs {
    border-bottom: none;
}

.card-header.bg-black .nav-tabs .nav-link {
    color: rgba(255, 255, 255, 0.7);
    border: none;
    border-bottom: 3px solid transparent;
    background: transparent;
    padding: 0.75rem 1rem;
    margin-right: 0.5rem;
}

.card-header.bg-black .nav-tabs .nav-link:hover {
    color: #fff;
    border-bottom-color: rgba(255, 255, 255, 0.3);
}

.card-header.bg-black .nav-tabs .nav-link.active {
    color: #fff;
    background: transparent;
    border-bottom: 3px solid #dc3545;
}

.card-header.bg-black .nav-tabs .nav-link.text-warning {
    color: #ffc107 !important;
}

.card-header.bg-black .nav-tabs .nav-link.text-danger {
    color: #dc3545 !important;
}

/* Settings Tabs */
#settingsTab .nav-link {
    font-weight: 500;
}

/* Temporary Account Features */
.temp-feature-card {
    height: 100%;
    transition: all 0.3s ease;
}

.temp-feature-card:hover {
    transform: translateY(-5px);
}

/* Form Switches */
.form-switch .form-check-input:checked {
    background-color: #dc3545;
    border-color: #dc3545;
}

/* Chart Container */
.chart-container {
    position: relative;
    height: 300px;
    width: 100%;
}

/* Data Table Actions */
.table-actions {
    display: flex;
    gap: 5px;
}

/* Loading Overlay */
.loading-overlay {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: rgba(255, 255, 255, 0.8);
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 1000;
    border-radius: 5px;
}

/* Responsive Adjustments */
@media (max-width: 768px) {
    .dashboard-card {
        margin-bottom: 20px;
    }

    .table-actions {
        flex-direction: column;
    }

    /* Responsive activity adjustments removed */
}

/* Print Styles */
@media print {
    .no-print {
        display: none !important;
    }

    .report-preview {
        border: none;
        padding: 0;
    }
}

/* Animation for new items */
@keyframes highlight {
    0% {
        background-color: rgba(220, 53, 69, 0.3);
    }

    100% {
        background-color: transparent;
    }
}

.highlight-new {
    animation: highlight 2s ease-out;
}
